mapper.select:
SELECT DISTINCT p.* FROM t_sys_role_permission rp LEFT JOIN t_sys_permission p ON rp.permissionId = p.id LEFT JOIN t_sys_role r ON rp.roleId = r.id WHERE r.id IN
#{roleId}
AND p.parentId = #{parentId, jdbcType=BIGINT} AND p.type = 0
